import React from "react";
import { connect } from "react-redux";
import * as boardActions from "../../actions/BoardActions";
import Board from "./Board";

const mapStateToProps = (state, ownProps) => {
  let boardId;
  let card;
  const { url } = ownProps.match;
  if (url.match(new RegExp("^/boards/"))) {
    boardId = ownProps.match.params.id;
  } else {
    card = state.cards.find(card => card.id === ownProps.match.params.id);
    if (card) {
      boardId = card.boardId;
    } else {
      boardId = null;
    }
  }
  if (boardId) {
    console.log("boards", state.boards);
    console.log("boardId", boardId)
    return {
      board: state.boards.find(board => board.id === boardId),
      card: card,
      boardId: boardId,
      user: state.user,
      loading: state.loading
    };
  } else {
    return {
      board: null,
      card: card,
      boardId: boardId,
      user: state.user,
      loading: state.loading
    };
  }
};

const mapDispatchToProps = dispatch => {
  return {
    onFetchBoard: (token, boardId, callback) => {
      dispatch(boardActions.fetchBoard(token, boardId, callback));
    }
  };
};

class BoardContainer extends React.Component {
  render() {
    return (
      <Board
        board={this.props.board}
        card={this.props.card}
        boardId={this.props.boardId}
        user={this.props.user}
        loading={this.props.loading}
        match={this.props.match}
        onFetchBoard={this.props.onFetchBoard}
      />
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(BoardContainer);
